sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();

// Query the database for NeedIt Task records with Due date field values older
// then the current time.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var overdueNITries = new GlideRecord('x_58872_needit_needit_try');
overdueNITries.addQuery('due_date','<=',rightNow);
overdueNITries.addQuery('state','!=',3);

overdueNITries.query();
// Write a log message for each overdue NeedIt Task Record
while(overdueNITries.next()){
	gs.info("Overdue NeedIt Task record = " + overdueNITries.number);
	gs.eventQueue('x_58872_needit.overdueNeedItTry',overdueNITries,overdueNITries.number,gs.getUserName());
}
